import React from 'react';
import { Link } from 'react-router-dom';
import './Cart.css';

function Cart() {
  // Example cart data (replace with dynamic data from state or props)
  const cartItems = [
    {
      id: 1,
      name: 'Hoodie',
      price: 50.0,
      quantity: 2,
      image: './img/product01.png',
    },
    {
      id: 2,
      name: 'T-Shirt',
      price: 25.0,
      quantity: 1,
      image: './img/product02.png',
    },
  ];

  // Calculate subtotal
  const subtotal = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
  const tax = subtotal * 0.1;
  const shipping = 5.0;
  const total = subtotal + tax + shipping;

  // Function to handle ABA Payway checkout
  const handleCheckout = async () => {
    const merchantId = 'your_merchant_id'; // Replace with your merchant ID
    const publicKey = 'your_public_key'; // Replace with your public key
    const tranId = `TXN-${Date.now()}`; // Generate a unique transaction ID
    const reqTime = new Date().toISOString().replace(/[-:T.]/g, '').slice(0, 14); // Current timestamp in YYYYmmddHis format

    // Prepare items for the transaction
    const items = cartItems.map((item) => ({
      name: item.name,
      quantity: item.quantity.toString(),
      price: item.price.toFixed(2),
    }));
    const itemsBase64 = btoa(JSON.stringify(items));

    // Generate the hash
    const hashString = `${reqTime}${merchantId}${tranId}${total.toFixed(2)}${itemsBase64}`;
    const hash = btoa(
      require('crypto')
        .createHmac('sha512', publicKey)
        .update(hashString)
        .digest('hex')
    );

    // Prepare the request body
    const formData = new FormData();
    formData.append('req_time', reqTime);
    formData.append('merchant_id', merchantId);
    formData.append('tran_id', tranId);
    formData.append('amount', total.toFixed(2));
    formData.append('items', itemsBase64);
    formData.append('currency', 'USD'); // Change to KHR if needed
    formData.append('payment_option', 'abapay');
    formData.append('hash', hash);

    try {
      // Make the API request to ABA Payway
      const response = await fetch('https://checkout-sandbox.payway.com.kh/api/payment-gateway/v1/payments/purchase', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (data.status.code === '00') {
        // Redirect to the ABA Payway checkout page or handle the response
        window.location.href = data.checkout_url; // Replace with the actual response field
      } else {
        alert(`Checkout failed: ${data.status.message}`);
      }
    } catch (error) {
      console.error('Error during checkout:', error);
      alert('An error occurred during checkout. Please try again.');
    }
  };

  return (
    <div className="cart-page">
      <h1>Your Cart</h1>
      <div className="cart-container">
        {/* Cart Items List */}
        <div className="cart-items">
          {cartItems.map((item) => (
            <div key={item.id} className="cart-item">
              <div className="item-image">
                <img src={item.image} alt={item.name} />
              </div>
              <div className="item-details">
                <h3>{item.name}</h3>
                <p>Price: ${item.price.toFixed(2)}</p>
                <p>Quantity: {item.quantity}</p>
                <button className="remove-item">Remove</button>
              </div>
              <div className="item-total">
                <p>${(item.price * item.quantity).toFixed(2)}</p>
              </div>
            </div>
          ))}
        </div>

        {/* Cart Summary */}
        <div className="cart-summary">
          <h2>Order Summary</h2>
          <div className="summary-details">
            <p>Subtotal: ${subtotal.toFixed(2)}</p>
            <p>Shipping: $5.00</p>
            <p>Tax: ${tax.toFixed(2)}</p>
            <hr />
            <h3>Total: ${total.toFixed(2)}</h3>
          </div>
          <div className="cart-actions">
            <Link to="/" className="continue-shopping">
              Continue Shopping
            </Link>
            <button className="checkout-button" onClick={handleCheckout}>
              Proceed to Checkout
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Cart;
